(()=>{var c=document.getElementById("spis-tel"),t=document.getElementById("wlasciwy-spis-tel");c.addEventListener("click",()=>{t.classList.contains("show")?(t.style.height=`${t.scrollHeight}px`,setTimeout(()=>{t.style.height="0",t.style.opacity="0"},10),t.classList.remove("show")):(t.style.height=`${t.scrollHeight}px`,t.style.opacity="1",setTimeout(()=>{t.style.height="auto"},500),t.classList.add("show"))});var s=document.querySelectorAll(".stat");s.length===4&&fetch("../stats.json").then(e=>e.json()).then(e=>{let o=(l,n)=>(Math.round(l/n)*n).toString().replace(/\B(?=(\d{3})+(?!\d))/g,".");s[0].textContent=o(e.total_users,100),s[1].textContent=o(e.exercises,20),s[2].textContent=o(e.views,1e4),s[3].textContent=o(e.code_lines,1e4)}).catch(e=>console.error(e));})();
